import React, { useState, useEffect } from 'react';
import postsData from '../data/posts'; // Изменено на postsData, чтобы избежать конфликта с переменной состояния posts
import styles from './SearchPage.module.css';

const SearchPage = () => {
  const [posts, setPosts] = useState(postsData);

  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    // Функция для сортировки по дате
    const sortByDate = (a, b) => new Date(b.date) - new Date(a.date);

    // Сортировка и фильтрация постов при изменении массива или строки поиска
    const filteredPosts = posts
      .filter(
        (post) =>
          post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          post.text.toLowerCase().includes(searchTerm.toLowerCase()) ||
          post.date.includes(searchTerm)
      )
      .sort(sortByDate);

    setSearchResults(filteredPosts);
  }, [searchTerm, posts]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchClick = () => {
    // Обработчик нажатия кнопки "Szukaj"
    // Здесь можно добавить дополнительную логику поиска, если необходимо
    // В данном случае, поиск происходит автоматически при изменении введенного текста
  };


  return (
    <div>
      <h1>Страница постов</h1>
      <div className={styles.inputWithButton}>
        <input
          type="text"
          placeholder="Wpisz tekst do wyszukania"
          value={searchTerm}
          onChange={handleSearchChange}
          className={styles.input}
        />

      
        <button onClick={() => {}} className={styles.inputButton}>
          Szukaj
        </button>
     
    </div>
   
    
      <ul>
        {searchResults.map((post) => (
          <li key={post.id}>
            <strong>{post.title}</strong>
            <p>{post.text}</p>
            
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchPage;
