import React, { useState } from 'react'
import Button from '../Button/Button'
import Post from '../Post/Post'
import TopPost from '../TopPost/TopPost'
import SidebarPosts from '../SidebarPosts/SidebarPosts'
import FilteredPosts from '../FilteredPosts/FilteredPosts'
import Input from '../Input/Input'


import topPosts from '../../data/topPosts'
import posts from '../../data/posts'
import sidebarPosts from '../../data/sidebarPosts'
import styles from './Posts.module.css'

const filterPosts = (allPosts, searchText) => {
  return allPosts.filter((post) => {
    const searchLower = searchText.toLowerCase();
    const postTitleLower = (post.title || '').toLowerCase(); // Добавляем проверку на существование
    const postTextLower = (post.text || '').toLowerCase(); // Добавляем проверку на существование
    const postDateLower = (post.date || '').toLowerCase(); // Добавляем проверку на существование

    return (
      postTitleLower.includes(searchLower) ||
      postTextLower.includes(searchLower) ||
      postDateLower.includes(searchLower) ||
      postDateLower.replace(/[^\w\s]/gi, '').includes(searchLower)
    );
  });
};



const Posts = () => {
  // Используем состояние для отслеживания количества видимых постов
  const [visiblePosts, setVisiblePosts] = useState(4) // Начинаем с отображения 4 постов
 
  const [filteredPosts, setFilteredPosts] = useState(posts);
  const [searchText, setSearchText] = useState('');

  // Обработчик для отображения большего количества постов
  const handleShowMore = () => {
    setVisiblePosts((prevVisiblePosts) => prevVisiblePosts + 4) // Увеличиваем количество отображаемых постов на 2
  }
  const handleSearch = () => {
    // Устанавливаем отфильтрованные посты в состояние
    setFilteredPosts(filterPosts(posts, searchText));
  };

  const handleSearchComplete = () => {
    setSearchText(''); // Очищаем состояние searchText
  };

  return (
    // Общий контейнер для постов
    <div className={styles.postsContainer}>
      <div className={styles.postsRow}>
        <div className={styles.postsLeft}>
          {/* Рендерим TopPost перед маппингом постов */}
          <TopPost topPost={topPosts[0]} />

          <div className={styles.posts}>
            {/* Маппим массив постов для их отображения */}
            {posts.slice(0, visiblePosts).map((post) => (
              <div key={post.id} className={styles.posts}>
                {/* Передаем компоненту Post информацию о посте */}
                <Post post={post} />
              </div>
            ))}
          </div>
        </div>
        <div className={styles.postsRight}>
        <Input onSearch={handleSearch} onSearchComplete={handleSearchComplete} setFilteredPosts={setFilteredPosts} posts={posts} />

        {/* Переместите FilteredPosts внутрь условия, чтобы он отображался только после нажатия "Искать" */}
        {searchText && <FilteredPosts filteredPosts={filteredPosts} />}
        
          <SidebarPosts sidebarPosts={sidebarPosts} />
        </div>
      </div>
      {/* Показываем кнопку "Показать ещё", если есть ещё посты для отображения */}
      {visiblePosts < posts.length && (
        <Button onClick={handleShowMore}>Show more</Button>
      )}
    </div>
  )
}

export default Posts
