// src/pages/Gallery.js
import React, { useState } from 'react';
import Post from '../components/Post/Post';
import SidebarPosts from '../components/SidebarPosts/SidebarPosts';
import Button from '../components/Button/Button';

function Gallery({ items, isLoading }) {
  const [searchValue, setSearchValue] = useState('');
  const [visiblePosts, setVisiblePosts] = useState(4);

  const onChangeSearchInput = (e) => {
    setSearchValue(e.target.value);
  };

  const handleShowMore = () => {
    setVisiblePosts((prevVisiblePosts) => prevVisiblePosts + 4);
  };

  const renderItems = () => {
    const filteredItems =
      items &&
      items.filter((item) =>
        item?.title?.toLowerCase().includes(searchValue.toLowerCase())
      );
  
    // Убедитесь, что filteredItems существует перед вызовом slice
    const displayedItems = isLoading ? [...Array(8)] : (filteredItems || []).slice(0, visiblePosts);
  
    return displayedItems.map((item, index) => (
      <Post
        key={index}
        loading={isLoading}
        {...item && {
            id: item.id,
            title: item.title,
            // Другие свойства
          }}
      />
    ));
  };

  return (
    <div className="content p-40 ">
      <div className="d-flex align-center justify-between mb-40">
        <h1>
          {searchValue ? `Search by request: "${searchValue}"` : `All sneakers`}
        </h1>
        <div className="search-block d-flex">
          <img src="../../../public/img/search.svg" alt="Search" />
          {searchValue && (
            <img
              onClick={() => setSearchValue('')}
              className="clear cu-p"
              src="../../../public/img/btn-remove.svg"
              alt="Clear"
            />
          )}
          <input
            onChange={onChangeSearchInput}
            value={searchValue}
            placeholder="Search"
          />
        </div>
      </div>

      <div className="d-flex flex-wrap">
        {renderItems()}
      </div>

      {visiblePosts < items.length && (
        <Button onClick={handleShowMore}>Show more</Button>
      )}

      <SidebarPosts sidebarPosts={items} />
    </div>
  );
}

export default Gallery;
