import { useState } from 'react';
import styles from './Input.module.css';
import FilteredPosts from '../FilteredPosts/FilteredPosts';

const Input = ({ onSearch, setFilteredPosts, posts}) => {
  const [searchText, setSearchText] = useState('');

  const handleInputChange = (e) => {
    setSearchText(e.target.value);
  };

  const handleSearch = () => {
    // Устанавливаем отфильтрованные посты в состояние
    setFilteredPosts(filterPosts(posts, searchText));
    console.log('Search Text:', searchText);
    onSearch(searchText, setFilteredPosts, posts);
    setSearchText(''); // Очищаем значение после поиска
  };


  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className={styles.inputWithButton}>
      <input
        type="text"
        placeholder="Wpisz tekst do wyszukania"
        value={searchText}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress} // Добавляем обработчик нажатия клавиши
        className={styles.input}
      />
      <button onClick={handleSearch} className={styles.inputButton}>
        Szukaj
      </button>
    </div>
  );
};

export default Input;
