// Импорт React и других компонентов
import { Link } from 'react-router-dom'
import NotFound from '../NotFound/NotFound'
import styles from './Post.module.css' // Импорт стилей для компонента Post

// Компонент Post
const Post = ({ post, isSinglePost }) => {
  // Деструктуризация свойств поста
  const { id, title, img, text } = post
  
  // Если пост не передан, отображаем ссылку на страницу NotFound
  if (!post) {
    return (
      // Контейнер для отображения страницы NotFound
      <div className={styles.postContainer.postImage}>
        <NotFound />
      </div>
    )
  }


  // Определяем, нужно ли отобразить полный текст поста или его сокращенную версию
  const displayedText = isSinglePost ? text : text.slice(0, 100) + '...'

  // Используем литералы шаблонов для создания уникального URL поста
  return (
    // Основной контейнер поста, с классами, зависящими от типа отображения
    <div
      className={`${styles.postContainer} ${
        isSinglePost ? styles.singlePost : ''
      }`}
    >
      {/* Ссылка на страницу с подробной информацией о посте */}
      <Link
        to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`}
        className={styles.postLink}
      >
        <div className={styles.postImageWrapper}>

        {/* Изображение поста */}
        <img className={styles.postImage} src={img} alt={title} />
        </div>
        {/* Заголовок поста */}
        <h3 className={styles.postTitle}>{title}</h3>
        {/* Контейнер для полного текста поста */}

        {/* Параграф с текстом поста (обрезанным) */}
        <p className={styles.postText}>{displayedText}</p>
      </Link>
    </div>
  )
}

// Экспорт компонента Post
export default Post
