import { Link } from 'react-router-dom';
import { useState } from 'react';
import styles from './Post.module.css'

const Post = ({ post }) => {
  const { imageUrl, title, text, date } = post;
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };
  // Если пост не передан, отображаем ссылку на страницу NotFound
  if (!post) {
    return (
      // Контейнер для отображения страницы NotFound
      <div className={styles.postContainer.postImage}>
        <NotFound />
      </div>
    )
  }
 

  return (
    <Link to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`} className={styles.postLink} onClick={toggleExpand}>
      <div className={styles.post}>
        <h3>{title}</h3>
        <h4>{date}</h4>
        <img src={imageUrl} alt={title} className={styles.postImage} />
        <p>{isExpanded ? text : `${text.slice(0, 200)}...`}</p>
      </div>
    </Link>
  );
}

export default Post;
