import { Link } from 'react-router-dom'
import './Post.css'
import NotFound from '../NotFound/NotFound'

const Post = ({ post, showFullText }) => {
  if (!post) {
    return <Link to="/NotFound">NotFound</Link>
  }
  const { id, title, img, text } = post
  const displayedText = showFullText ? text : text.slice(0, 200) + '...'
  // const truncatedText = text.length > 200 ? text.slice(0, 200) + '...' : text
  return (
    <div className="post">
      <Link to={`/posts/${id}${title}`}>
        <img className="post-image" src={img} alt={title} />
        <h3>{title}</h3>
        <p>{displayedText}</p>
      </Link>
    </div>
  )
}

export default Post
