// SearchPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Button from '../Button/Button';
import Post from '../Post/Post';
import TopPost from '../TopPost/TopPost';
import SidebarPosts from '../SidebarPosts/SidebarPosts';
import styles from './SearchPage.module.css'; // Create a separate CSS file for styling if needed

const SearchPage = () => {
  const [allPosts, setAllPosts] = useState({ category: [], interesting: [] });
  const [visiblePosts, setVisiblePosts] = useState(4);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:3002/posts');
        const allFetchedPosts = response.data;

        setAllPosts({
          category: allFetchedPosts.filter(post => post.category !== 'Interesting'),
          interesting: allFetchedPosts.filter(post => post.category === 'Interesting'),
        });
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  useEffect(() => {
    // Filter posts based on the search query
    const filteredPosts = allPosts.category.filter(post =>
      post.title.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setSearchResults(filteredPosts);
  }, [searchQuery, allPosts.category]);

  const handleShowMore = () => {
    setVisiblePosts(prevVisiblePosts => prevVisiblePosts + 4);
  };

  const topPost = searchResults.find(post => post.place === 'top');

  return (
    <div className={styles.searchPageContainer}>
      <div className={styles.postsRow}>
        <div className={styles.postsLeft}>
          {topPost && <TopPost topPost={topPost} />}

          <div className={styles.posts}>
            {searchResults
              .filter(post => post.place !== 'top')
              .slice(0, visiblePosts)
              .map(post => (
                <div key={post.slug} className={styles.posts}>
                  <Post key={post.slug} post={post} />
                </div>
              ))}
          </div>
        </div>
        <div className={styles.postsRight}>
          <SidebarPosts sidebarPosts={allPosts.interesting.slice(0)} />
        </div>
      </div>
      {visiblePosts < searchResults.length && (
        <Button onClick={handleShowMore}>Show more</Button>
      )}

      {/* Search input field */}
      <input
        type="text"
        placeholder="Search..."
        value={searchQuery}
        onChange={e => setSearchQuery(e.target.value)}
      />
    </div>
  );
};

export default SearchPage;
