import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import Button from '../components/Button/Button';
import Post from '../components/Post/Post';
import filterPosts from './Posts';  // Импортируйте функцию filterPosts из вашего Posts.js
import styles from './SearchResultsPage.module.css';  // Создайте CSS-модуль для стилей страницы

const SearchResultsPage = ({ posts }) => {
  const history = useHistory();
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect(() => {
    const searchQuery = new URLSearchParams(history.location.search).get('q');
    if (searchQuery) {
      setSearchText(searchQuery);
      setFilteredPosts(filterPosts(posts, searchQuery));
    }
  }, [history.location.search, posts]);

  return (
    <div className={styles.searchResultsContainer}>
      <h2>Search Results for: {searchText}</h2>
      
      <div className={styles.searchResults}>
        {filteredPosts.map((post) => (
          <div key={post.id} className={styles.searchResult}>
             <div className={styles.post}>
        <img src={img} alt={title} className={styles.postImage} />
        <div className={styles.postTitle}>
          <h3>{title}</h3>
        </div>
        <div className={styles.postText}>
        
          {isExpanded ? text : `${text.slice(0, 100)}...`}
       
        </div>
        <h4>{date}</h4>
      </div>
          </div>
        ))}
      </div>

      <Button onClick={() => history.push('/posts')}>Back to Posts</Button>
    </div>
  );
};

export default SearchResultsPage;
