// SearchResultsPage.js
import React, { useEffect, useState } from 'react';
import Button from '../components/Button/Button';
import Post from '../components/Post/Post';
import filterPosts from '../components/Posts/Posts';

import posts from '../data/posts';

const SearchResultsPage = ({ posts, goBackToPosts }) => {
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect(() => {
    // Your existing logic to get search query and filter posts
    const searchQuery = new URLSearchParams(window.location.search).get('q');
    if (searchQuery) {
      setSearchText(searchQuery);
      setFilteredPosts(filterPosts(posts, searchQuery));
    }
  }, [posts]);

  return (
    <div className={styles.searchResultsContainer}>
      <h2>Search Results for: {searchText}</h2>

      <div className={styles.searchResults}>
        {filteredPosts.map((post) => (
          <div key={post.id} className={styles.searchResult}>
            <Post post={post} />
          </div>
        ))}
      </div>

      <Button onClick={goBackToPosts}>Back to Posts</Button>
    </div>
  );
};

export default SearchResultsPage;
