import React from 'react'
import { Link } from 'react-router-dom'
import './Post.css'

const Post = ({ post, showFullText, isSinglePost }) => {
  // Если пост не передан, отображаем ссылку на страницу NotFound
  if (!post) {
    return <Link to="/notFound" className={styles.postLink}>NotFound</Link>;
  
  }

  // Деструктуризация свойств поста
  const { id, title, img, text } = post

  // Определяем, нужно ли отобразить полный текст поста или его сокращенную версию
  const displayedText = showFullText ? text : text.slice(0, 200) + '...'

  // Используем литералы шаблонов для создания уникального URL поста
  return (
    <div className={`post ${isSinglePost ? 'single-post' : ''}`}>
      {/* Ссылка на страницу с подробной информацией о посте */}
      <Link to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`} className={styles.postLink}>
        <img className={styles.postImage} src={img} alt={title} />
        <h3 className={styles.postTitle}>{title}</h3>
        <div className={isSinglePost ? styles.postContentFull : styles.postContent}>
          <p className={styles.postText}>{displayedText}</p>
        </div>
      </Link>
    </div>
  )
}

export default Post
