import React, { useState } from 'react';
import '../path-to-your-css/Header.css'; // Update the path to your CSS file
import { Link } from 'react-router-dom';

const Header = () => {
  const [isNavigationOpen, setNavigationOpen] = useState(false);
  const [isLanguageSelectorOpen, setLanguageSelectorOpen] = useState(false);

  const handleToggleNavigation = () => {
    setNavigationOpen((prev) => !prev);
    setLanguageSelectorOpen(false); // Close language selector when opening navigation
  };

  const handleToggleLanguageSelector = () => {
    setLanguageSelectorOpen((prev) => !prev);
    setNavigationOpen(false); // Close navigation when opening language selector
  };

  return (
    <header>
      <div className="header-content">
        <div className="_md-f_y5r2b_119 _selectorsContainer_y5r2b_114">
          {/* City Selector */}
          <div className="_container_u8fp1_4" data-testid="city-selector_testID">
            <input
              type="radio"
              name="masthead-dropdowns"
              data-city-selector="true"
              className="_checkbox_u8fp1_1"
              id="checkbox-city-selector"
              data-animated-chevron-input="true"
              data-testid="city-selector-cta_testID"
            />
            <label htmlFor="checkbox-city-selector">
              <a className="_cta_u8fp1_1">
                <span>New York</span>
                <span className="_animatedChevron_c3wq1_1 _small_c3wq1_19"></span>
              </a>
            </label>
            {/* City Selector List */}
            <div className="_listContainer_u8fp1_1" data-testid="city-selector-list_testID">
              {/* City Selector List items */}
              {/* ... */}
            </div>
          </div>

          {/* Language Selector */}
          <div className="_container_p7aiv_4" data-testid="language-selector_testID">
            <input
              type="radio"
              name="masthead-dropdowns"
              data-language-selector="true"
              id="checkbox-language-selector"
              className="_checkbox_p7aiv_1"
              data-animated-chevron-input="true"
              data-testid="language-selector-cta_testID"
              value="language-selector"
            />
            <label htmlFor="checkbox-language-selector">
              <a className="_cta_p7aiv_1">
                EN
                <span className="_animatedChevron_c3wq1_1 _small_c3wq1_19"></span>
              </a>
            </label>
            {/* Language Selector List */}
            <ul className="_ul_p7aiv_1" data-testid="language-selector-list_testID">
              {/* Language Selector List items */}
              {/* ... */}
            </ul>
          </div>
        </div>

        {/* Logo */}
        <h1>Auto Wroclaw</h1>

        {/* Auth Links */}
        <div className="auth-links">
          <Link to="/registration">Registration</Link>
          <span> | </span>
          <Link to="/login">Login</Link>
        </div>
      </div>
    </header>
  );
};

export default Header;
