import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import NotFound from '../components/NotFound/NotFound';
import styles from './PostPage.module.css';

const PostPage = () => {
  const { postId } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    // Запрос к серверу для получения данных поста по его идентификатору
    axios
      .get(`http://localhost:5000/posts/${postId}`)
      .then((response) => {
        setPost(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, [postId]);

  if (!post) {
    // Если пост не найден, отобразим страницу NotFound
    return <NotFound />;
  }

  return (
    <div className={styles.postPage}>
      <div className={styles.postContainer}>
        <img src={post.img} alt={post.title} className={styles.postPageImage} />
        <div className={styles.postPageTitle}>
          <h3>{post.title}</h3>
        </div>
        <div className={styles.postPageText}>{post.text}</div>
      </div>
    </div>
  );
};

export default PostPage;
