// NotFound.js
import React, { useState, useEffect } from 'react';
import styles from './NotFound.module.css';

const NotFound = () => {
  const notFoundImg = '../../../img/photo/NotFound.jpg'; // Путь к изображению Not Found
  const [errorInfo, setErrorInfo] = useState(null);

  useEffect(() => {
    // Предположим, что у вас есть какая-то логика обработки ошибок в другом месте вашего кода
    try {
      // Код, который может вызвать ошибку
      throw new Error('Пример сообщения об ошибке');
    } catch (error) {
      setErrorInfo(error.message);
    }
  }, []); // [] гарантирует выполнение useEffect только после первого рендера

  const notFound = {
    id: 1,
    img: '../../../img/photo/NotFound.jpg',
    title: errorInfo || 'Страница не найдена',
    text: errorInfo ? 'Произошла ошибка. Пожалуйста, проверьте консоль для дополнительной информации.' : 'Извините, запрашиваемая вами страница не существует.',
    date: '2023-12-08', // Пример даты
  };

  const { img, title, text } = notFound;

  return (
    <div className={styles.postPage}>
      <div className={styles.notFoundContainer}>
        <img src={notFoundImg} alt={title} className={styles.notFoundImage} />
        <div className={styles.notFoundPageTitle}>
          <h3>{title}</h3>
        </div>
        <div className={styles.postPageText}>{text}</div>
      </div>
    </div>
  );
};

export default NotFound;
