// SearchPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './SearchPage.module.css';
import Post from '../components/Post/Post';

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchClick = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`http://localhost:3002/posts?search=${searchTerm}`);
      const searchData = response.data;
      setSearchResults(searchData);
    } catch (error) {
      console.error('Error fetching search results:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={styles.searchContainer}>
      <div className={styles.searchRow}>
        <div className={styles.searchLeft}>
          <h1>Все посты - за все времена</h1>
          <div className={styles.inputWithButton}>
            <input
              type="text"
              placeholder="Wpisz tekst do wyszukania"
              value={searchTerm}
              onChange={handleSearchChange}
              className={styles.input}
            />
            <button className={styles.inputButton} onClick={handleSearchClick} disabled={loading}>
              {loading ? 'Wyszukiwanie...' : 'Szukaj'}
            </button>
          </div>
          {searchResults.length > 0 && (
            <ul>
              {searchResults.map((post) => (
                <div key={post.id} className={styles.search}>
                  <Post post={post} />
                </div>
              ))}
            </ul>
          )}
          {searchResults.length === 0 && !loading && (
            <p>Brak wyników wyszukiwania</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default SearchPage;
