// SidebarPosts.js
import React from 'react';
import styles from './SidebarPosts.module.css';

const SidebarPosts = ({ sidebarPosts }) => {
  const visiblePosts = 4;

  return (
    <Link
    to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`}
    className={styles.postLink}
    onClick={toggleExpand}
  >
    <div className={styles.sidebarPostsContainer}>
      <div className={styles.sidebarPosts}>
        {/* Маппим массив постов для их отображения */}
        {sidebarPosts.slice(0, visiblePosts).map((sidebarPost) => (

          <div key={sidebarPost.id} className={styles.sidebarPost}>
            {/* Передаем компоненту Post информацию о посте */}
            <div className={styles.sidebarPost}>
              <img src={sidebarPost.img} alt={sidebarPost.title} className={styles.sidebarPostImage} />
              <div className={styles.sidebarPostTitle}>
                <h3>{sidebarPost.title}</h3>
              </div>
              <h4>{sidebarPost.date}</h4>
            </div>
          </div>
        ))}
      </div>
    </div>
    </Link>
  );
};

export default SidebarPosts;
