// SidebarPosts.js
import React from 'react';
import { Link } from 'react-router-dom';

import sidebarPosts from '../../data/sidebarPosts';



import styles from './SidebarPosts.module.css'; // Импортируем стили для компонента SidebarPosts

const SidebarPosts = () => {

  const { img, title, date } = sidebarPosts

  return (
    <Link
    to={`/posts/${sidebarPosts.id}-${sidebarPosts.title.replace(/\s+/g, '-')}`}
    className={styles.postLink}
      >
    <div className={styles.post}>
      <img src={img} alt={title} className={styles.postImage} />
      <div className={styles.postTitle}>
        <h3>{title}</h3>
      </div>
      <h4>{date}</h4>
    </div>
  </Link>
  );
};

export default SidebarPosts;
