import { Link } from 'react-router-dom'
import { useState, useEffect } from 'react'
import axios from 'axios'
import styles from './Post.module.css'

const Post = ({ post }) => {
  const { img, title, text, date, id } = post
  const [isExpanded, setIsExpanded] = useState(false)
  const [postDetails, setPostDetails] = useState(null)
console.log(posts)
  const toggleExpand = () => {
    setIsExpanded(!isExpanded)
  }

  useEffect(() => {
    // Запрос к серверу для получения подробностей поста
    axios
      .get(`http://localhost:5000/posts/${id}`)
      .then((response) => {
        setPostDetails(response.data)
      })
      .catch((error) => {
        console.error(error)
      })
  }, [id])

  const toLink = postDetails
    ? `/posts/${postDetails.id}-${postDetails.title.replace(/\s+/g, '-')}`
    : '#' // Можете установить какой-то дефолтный URL, если данные еще не загружены


  return (
    <Link to={toLink} className={styles.postLink} onClick={toggleExpand}>
      <div className={styles.post}>
        <img src={img} alt={title} className={styles.postImage} />
        <div className={styles.postTitle}>
          <h3>{title}</h3>
        </div>
        <div className={styles.postText}>
          {isExpanded ? text : `${text.slice(0, 100)}...`}
        </div>
        <h4>{date}</h4>
      </div>
    </Link>
  )
}

export default Post
