const Post = ({ post }) => {
    if (!post) {
        return <p>Пост не найден</p>;
      }
  const { title, img, text } = post
  return (
    <div>
      <img src={img} />
      <h3>{title}</h3>
      <h4>{text}</h4>
    </div>
  )
}

export default Post
