import { Link } from 'react-router-dom'
import './Post.css'

const Post = ({ post }) => {
  if (!post) {
    return <p>Пост не найден</p>
  }
  const { id, title, img, text } = post
  const truncatedText = text.length > 200 ? text.slice(0, 200) + '...' : text
  return (
    <div className="post">
      <Link to={`/posts/${id}`}>
        <img className="post-image" src={img} alt={title} />
        <h3>{title}</h3>
        <p>{truncatedText}</p>
      </Link>
    </div>
  )
}

export default Post
