// PostPage.js
import React from 'react'
import { useParams } from 'react-router-dom'
import posts from '../data/posts' // Импортируйте ваши данные с постами
import Post from '../components/Post/Post'
import './PostPage.css' // Импортируем стили
import NotFound from '../components/NotFound/NotFound'

const PostPage = () => {
  const { postId } = useParams() // Используем хук useParams для получения параметра из URL

  // Найдите пост с помощью идентификатора postId
  const post = posts.find((p) => p.id === parseInt(postId, 10))

  if (!post) {
    return  <NotFound />;
    
  }

  // Возвращаем компонент поста, передавая информацию о посте и флаг showFullText

  return (
    <div className="post-page">
    {/* Передаем флаг isSinglePost */}
    <Post post={post} showFullText={true}  />
  </div>
  )
}

export default PostPage
