// SidebarPosts.js
import React from 'react';
import { Link } from 'react-router-dom';
import Post from '../components/Post/Post'; // Предполагается, что у вас есть компонент Post


import styles from './SidebarPosts.module.css'; // Импортируем стили для компонента SidebarPosts

// Массив данных для отображения в SidebarPosts
const sidebarPostsData = [
  // Ваши данные для каждого поста здесь
  // Например:
  { id: 1, img: 'url1', title: 'Заголовок 1' },
  { id: 2, img: 'url2', title: 'Заголовок 2' },
  { id: 3, img: 'url3', title: 'Заголовок 3' },
  { id: 4, img: 'url4', title: 'Заголовок 4' },
  { id: 5, img: 'url5', title: 'Заголовок 5' },
];

const SidebarPosts = () => {
  return (
    <div className={styles.sidebarPosts}>
      <h2>Популярные посты</h2>
      <div className={styles.postsContainer}>
        {sidebarPostsData.map((post) => (
          <Link key={post.id} to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`}>
            <Post post={post} customStyles={styles.sidebarPost} />
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SidebarPosts;
