import React, { useState } from 'react'
import Post from '../Post/Post'
import posts from '../../data/posts'
import './Posts.css'

const Posts = () => {
  const [visiblePosts, setVisiblePosts] = useState(2) // Начинаем с отображения 2 постов

  const handleShowMore = () => {
    setVisiblePosts((prevVisiblePosts) => prevVisiblePosts + 2) // Увеличиваем количество отображаемых постов на 2
  }
  return (
    <div className="posts-container">
      {posts.slice(0, visiblePosts).map((post) => (
        <div key={post.id} className="post-container">
        <Post post={post} />
      </div>
      ))}
      {visiblePosts < posts.length && (
        <button onClick={handleShowMore}>Показать ещё</button>
      )}
    </div>
  )
}

export default Posts
