// SidebarPosts.js
import React from 'react';
import { Link } from 'react-router-dom';

import sidebarPosts from '../../data/sidebarPosts'; // Предполагая, что sidebarPosts является массивом
import styles from './SidebarPosts.module.css';

const SidebarPosts = ({sidebarPost}) => {
  const { id, img, title, date } = sidebarPost; // Добавлено id
  const visiblePosts = 4;
  

   return (
    <>
    <div className={styles.sidebarPostsContainer}>
    <div className={styles.sidebarPosts}>
          {/* Маппим массив постов для их отображения */}
          {sidebarPost.slice(0, visiblePosts).map((sidebarPost) => (
            <div key={sidebarPost.id} className={styles.sidebarPosts}>
              {/* Передаем компоненту Post информацию о посте */}
              <div className={styles.sidebarPost}>
        <img src={img} alt={title} className={styles.sidebarPostImage} />
        <div className={styles.sidebarPostTitle}>
          <h3>{title}</h3>
        </div>
        <h4>{date}</h4>
      </div>
            </div>
          ))}
        </div>
        </div>
    </>
  );
};

export default SidebarPosts;
