// PostPage.js
import React from 'react';
import { useParams } from 'react-router-dom';
import posts from '../data/posts'; // Импортируйте ваши данные с постами
import './PostPage.css'; // Импортируем стили

const PostPage = () => {
  const { postId } = useParams();

  // Найдите пост с помощью идентификатора postId
  const post = posts.find((p) => p.id === parseInt(postId, 10));

  if (!post) {
    return <p>Пост не найден</p>;
  }

  const { title, img, text } = post;

  return (
    <div className="post-page">
    <img className="post-image" src={img} alt={title} />
    <h2 className="post-title">{title}</h2>
    <p className="post-text">{text}</p>
  </div>
  );
};

export default PostPage;
