import { Link } from 'react-router-dom'
import { useState } from 'react'
import styles from './Input.module.css'

const Input = ({ onSearch, setFilteredPostIndexes, posts }) => {
  const [searchText, setSearchText] = useState('');

  const handleInputChange = (e) => {
    setSearchText(e.target.value)
  }

  const handleSearch = () => {
    console.log('Запущен поиск');
    searchPosts(searchText, setFilteredPostIndexes, posts);
    setSearchText('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      console.log('Сработал Enter на Input')
      handleSearch()
    }
  }

  return (
    <div className={styles.inputWithButton}>
      <input
        type="text"
        placeholder="Wpisz tekst do wyszukania"
        value={searchText}
        onChange={handleInputChange}
        onKeyPress={handleKeyPress} // Добавляем обработчик нажатия клавиши
        className={styles.input}
      />
      <Link to={`/search`}>
        <button onClick={handleSearch} className={styles.inputButton}>
          Szukaj
        </button>
      </Link>
    </div>
  )
}

export default Input
