// SidebarPosts.js
import React from 'react';
import { Link } from 'react-router-dom';

import sidebarPosts from '../../data/sidebarPosts'; // Предполагая, что sidebarPosts является массивом
import styles from './SidebarPosts.module.css';

const SidebarPosts = ({sidebarPost}) => {
  const { id, img, title, date } = sidebarPosts[1]; // Добавлено id
  
  const [isExpanded, setIsExpanded] = useState(false)

  const toggleExpand = () => {
    setIsExpanded(!isExpanded)
  }
  return (
    <>
    <div className={styles.posts}>
          {/* Маппим массив постов для их отображения */}
          {sidebarPosts.slice(0, 4).map((post) => (
            <div key={post.id} className={styles.posts}>
              {/* Передаем компоненту Post информацию о посте */}
              <div className={styles.post}>
        <img src={img} alt={title} className={styles.postImage} />
        <div className={styles.postTitle}>
          <h3>{title}</h3>
        </div>
        <h4>{date}</h4>
      </div>
            </div>
          ))}
        </div>
    
    </>
  );
};

export default SidebarPosts;
