// NotFound.js
import React, { useEffect, useState } from 'react';
import styles from './NotFound.module.css';

const NotFound = () => {
  const notFoundImg = '../../../img/photo/NotFound.jpg'; // Путь к изображению Not Found
  const [errorTitle, setErrorTitle] = useState('Страница не найдена');
  const [errorText, setErrorText] = useState('');

  useEffect(() => {
    // Обработчик ошибок, который будет вызываться при возникновении ошибки
    const errorHandler = (event) => {
      setErrorTitle('Произошла ошибка');
      setErrorText(`Ошибка: ${event.message}`);
    };

    // Регистрация обработчика ошибок
    window.addEventListener('error', errorHandler);

    // Отмена регистрации обработчика при размонтировании компонента
    return () => {
      window.removeEventListener('error', errorHandler);
    };
  }, []);

  return (
    <div className={styles.postPage}>
      <div className={styles.notFoundContainer}>
        <img src={notFoundImg} alt={errorTitle} className={styles.notFoundImage} />
        <div className={styles.notFoundPageTitle}>
          <h3>{errorTitle}</h3>
        </div>
        <div className={styles.postPageText}>{errorText}</div>
      </div>
    </div>
  );
};

export default NotFound;
