import React from 'react';
import { Link } from 'react-router-dom';
import styles from './SidebarPosts.module.css';

const SidebarPosts = ({ sidebarPosts }) => {
  const visiblePosts = 4;

  return (
    <div className={styles.sidebarPostsContainer}>
      <div className={styles.sidebarPosts}>
        {/* Маппим массив постов для их отображения */}
        {sidebarPosts.slice(0, visiblePosts).map((post) => (
          <div key={post.id} className={styles.sidebarPost}>
            {/* Передаем компоненту Post информацию о посте */}
            <Link to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`} className={styles.postLink}>
              <img src={post.img} alt={post.title} className={styles.sidebarPostImage} />
              <div className={styles.sidebarPostTitle}>
                <h3>{post.title}</h3>
              </div>
              <h4>{post.date}</h4>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SidebarPosts;
