import React, { useState } from 'react';
import './Header.css'; // Import styles

const Header = () => {
  const [isNavigationOpen, setNavigationOpen] = useState(false);
  const [isLanguageSelectorOpen, setLanguageSelectorOpen] = useState(false);

  const handleToggleNavigation = () => {
    setNavigationOpen((prev) => !prev);
    setLanguageSelectorOpen(false); // Close language selector when opening navigation
  };

  const handleToggleLanguageSelector = () => {
    setLanguageSelectorOpen((prev) => !prev);
    setNavigationOpen(false); // Close navigation when opening language selector
  };

  return (
    <header>
      <div className="header-content">
        <div className="selectors-container">
          {/* City Selector */}
          <div className="container" data-testid="city-selector_testID">
            <input
              type="radio"
              name="masthead-dropdowns"
              data-city-selector="true"
              className="checkbox"
              id="checkbox-city-selector"
              data-animated-chevron-input="true"
              data-testid="city-selector-cta_testID"
            />
            <label htmlFor="checkbox-city-selector">
              <a className="cta">
                <span>New York</span>
                <span className="animatedChevron"></span>
              </a>
            </label>
            {/* City Selector List */}
            <div className="list-container" data-testid="city-selector-list_testID">
              {/* City Selector List items */}
              {/* ... */}
            </div>
          </div>

          {/* Language Selector */}
          <div className="container" data-testid="language-selector_testID">
            <input
              type="radio"
              name="masthead-dropdowns"
              data-language-selector="true"
              id="checkbox-language-selector"
              className="checkbox"
              data-animated-chevron-input="true"
              data-testid="language-selector-cta_testID"
              value="language-selector"
            />
            <label htmlFor="checkbox-language-selector">
              <a className="cta">
                EN
                <span className="animatedChevron"></span>
              </a>
            </label>
            {/* Language Selector List */}
            <ul className="ul" data-testid="language-selector-list_testID">
              {/* Language Selector List items */}
              {/* ... */}
            </ul>
          </div>
        </div>

        {/* Logo */}
        <h1>Auto Wroclaw</h1>

        {/* Auth Links */}
        <div className="auth-links">
          <a href="/registration">Registration</a>
          <span> | </span>
          <a href="/login">Login</a>
        </div>
      </div>
    </header>
  );
};

export default Header;
