// SidebarPosts.js
import React from 'react';
import { Link } from 'react-router-dom';
import sidebarPosts from '../../data/sidebarPosts';
import posts from '../../data/posts'
import Post from '../components/Post/Post'; // Предполагается, что у вас есть компонент Post


import styles from './SidebarPosts.module.css'; // Импортируем стили для компонента SidebarPosts

const SidebarPosts = () => {
  return (
    <div className={styles.sidebarPosts}>
      <h2>Популярные посты</h2>
      <div className={styles.postsContainer}>
        {sidebarPosts.map((post) => (
          <Link key={post.id} to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`}>
            <Post post={post} customStyles={styles.sidebarPost} />
          </Link>
        ))}
      </div>
    </div>
  );
};

export default SidebarPosts;
