import React, { useState, useEffect } from 'react';
import posts from '../data/posts';
import styles from 'S'

const SearchPage = () => {
  const [posts, setPosts] = useState([
    { id: 1, title: 'Заголовок 1', text: 'Текст поста 1', date: '2023-01-01' },
    { id: 2, title: 'Заголовок 2', text: 'Текст поста 2', date: '2023-02-01' },
    { id: 3, title: 'Заголовок 3', text: 'Текст поста 3', date: '2023-03-01' },
    // Добавьте другие посты по мере необходимости
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    // Функция для сортировки по дате
    const sortByDate = (a, b) => new Date(b.date) - new Date(a.date);

    // Сортировка и фильтрация постов при изменении массива или строки поиска
    const filteredPosts = posts
      .filter(
        (post) =>
          post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
          post.text.toLowerCase().includes(searchTerm.toLowerCase()) ||
          post.date.includes(searchTerm)
      )
      .sort(sortByDate);

    setSearchResults(filteredPosts);
  }, [searchTerm, posts]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  return (
    <div>
      <h1>Страница постов</h1>
      <>
    <div className={styles.inputWithButton}>
      <input
        type="text"
        placeholder="Wpisz tekst do wyszukania"
        value={searchValue}
        onChange={onChangeSearchInput}
        onKeyPress={handleKeyPress} // Добавляем обработчик нажатия клавиши
        className={styles.input}
        src="/img"
      />
      
        <button onClick={() => {}} className={styles.inputButton}>
          Szukaj
        </button>
     
    </div>
    <h1>{searchValue ? `Поиск по запросу: "${searchValue}"` : 'все посты'}</h1>
    </>
      <ul>
        {searchResults.map((post) => (
          <li key={post.id}>
            <strong>{post.title}</strong>
            <p>{post.text}</p>
            
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SearchPage;
