import { Link } from 'react-router-dom';
import { useState } from 'react';
import styles from './Post.module.css'

const Post = ({ post }) => {
  const { img, title, text, date } = post;
  const [isExpanded, setIsExpanded] = useState(false);

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
  };

 

  return (

    
    <Link to={`/posts/${post.id}-${post.title.replace(/\s+/g, '-')}`} className={styles.postLink} onClick={toggleExpand}>
      <div className={styles.post}>
        <img src={img} alt={title} className={styles.postImage} />
        <h3 className={styles.postTitle}>{title}</h3>
        <h4 className={styles.postTex}>{text}</h4>
        <p>{isExpanded ? text : `${text.slice(0, 50)}...`}</p>
      </div>
    </Link>
  );
}

export default Post;
